package week.week.year2023;

import java.util.Arrays;

public class 三数之和的多种可能923 {

    public int threeSumMulti(int[] arr, int target) {
        Arrays.sort(arr);
        int n = arr.length;
        long MOD = (long) 1e9 + 7;
        long ans = 0;
        for (int i = 0; i < n; i++) {
            int l = i + 1;
            int r = n - 1;
            while (l < r) {
                if (arr[i] + arr[l] + arr[r] == target && arr[l] != arr[r]) {
                    int leftNum = 1;
                    int rightNum = 1;
                    while (l + 1 < r && arr[l] == arr[l + 1]) {
                        l++;
                        leftNum++;
                    }
                    while (l <= r - 1 && arr[r] == arr[r - 1]) {
                        r--;
                        rightNum++;
                    }
                    ans = (ans + leftNum * rightNum) % MOD;
                    l++;
                    r--;
                } else if (arr[i] + arr[l] + arr[r] == target && arr[l] == arr[r]) {
                    int num = r - l + 1;
                    ans = (ans + num * (num - 1) / 2) % MOD;
                    break;
                } else if (arr[i] + arr[l] + arr[r] < target) {
                    l++;
                } else if (arr[i] + arr[l] + arr[r] > target) {
                    r--;
                }
            }
        }

        return (int) ans;
    }

}

